package twopointers;

/**
 * @author tianbo
 * @date 2019-02-28
 */
public class CheckInclusion {
    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.checkInclusion("ab", "eidbaooo"));
        System.out.println(solution.checkInclusion("ab", "eidboaoo"));
        System.out.println(solution.checkInclusion("aob", "eidboaoo"));
        System.out.println(solution.checkInclusion("abod", "eidboaoo"));
        System.out.println(solution.checkInclusion("abodi", "eidboaoo"));
        System.out.println(solution.checkInclusion("abodie", "eidboaoo"));
        System.out.println(solution.checkInclusion("abodieo", "eidboaoo"));
        System.out.println(solution.checkInclusion("abodieoo", "eidboaoo"));
        System.out.println(solution.checkInclusion("abodoieo", "eidboaoo"));
        System.out.println(solution.checkInclusion("abodieio", "eidboaoo"));

        String str1 = "";
        String str2 = "";
        System.out.println(solution.checkInclusion(str1, str2));
    }

    // 击败99.90%
    static class Solution {
        public boolean checkInclusion(String s1, String s2) {
            if (s1.length() > s2.length()) {
                return false;
            }

            char[] chars1 = s1.toCharArray();
            char[] chars2 = s2.toCharArray();

            int[] countS1 = new int[26];
            int[] countS2 = new int[26];

            for (int i = 0; i < chars1.length; i++) {
                countS1[chars1[i] - 'a']++;
                countS2[chars2[i] - 'a']++;
            }

            // 基本不会有这样的case出现
            if (equalsArray(countS1, countS2)) {
                return true;
            }

            for (int i = chars1.length; i < chars2.length; i++) {
                // s2的窗口开始滑动, 当新纳入一个元素时,原来的第一元素需要被剔除掉
                countS2[chars2[i] - 'a']++;
                countS2[chars2[i - chars1.length] - 'a']--;
                if (equalsArray(countS1, countS2)) {
                    return true;
                }
            }

            return false;
        }

        private boolean equalsArray(int[] source, int[] target) {
            if (source.length != target.length) {
                return false;
            }
            for (int i = 0; i < source.length; i++) {
                if (source[i] != target[i]) {
                    return false;
                }
            }
            return true;
        }
    }

    // 击败11.39%, 效率偏低
    static class Solution1 {
        public boolean checkInclusion(String s1, String s2) {
            int len1 = s1.length();
            int len2 = s2.length();
            if (len1 > len2) {
                return false;
            }

            int[] countStr2 = new int[26];

            // 时间复杂度为O(len1+len2)初步判断
            for (int i = 0; i < len2; i++) {
                countStr2[s2.charAt(i) - 'a']++;
            }

            for (int i = 0; i < len1; i++) {
                countStr2[s1.charAt(i) - 'a']--;
            }

            if (isAllZero(countStr2)) {
                return true;
            }

            // 时间复杂度O(len2*len1), 通常len1很小, 时间复杂度可近似看做O(n)
            int pa = 0, pb = pa + len1 - 1;
            while (pb < len2) {
                if (match(s2, pa, pb, s1)) {
                    return true;
                }
                pa++;
                pb = pa + len1 - 1;
            }
            return false;
        }

        private boolean isAllZero(int[] data) {
            for (int i = 0; i < data.length; i++) {
                if (data[i] != 0) {
                    return false;
                }
            }
            return true;
        }

        private boolean match(String source, int pa, int pb, String target) {
            int[] counts = new int[26];
            for (int i = pa; i <= pb; i++) {
                counts[source.charAt(i) - 'a']++;
            }

            int len = target.length();
            for (int i = 0; i < len; i++) {
                int index = target.charAt(i) - 'a';
                if (counts[index] <= 0) {
                    return false;
                }
                counts[index]--;
            }

            return isAllZero(counts);
        }
    }
}
